find_rule:
js:
var item = [];
Body = MY_URL.split('#');
var body = Body[2];
var referer = Body[1];
var h = 'https://api.bangumi.online/serve/home';

var data = JSON.parse(fetch(h, {
    headers: {
        'User-Agent': MOBILE_UA,
        'Referer': referer,
    },
    body: body,
    method: 'post'
})).data.list;
log(data)
for (var d of data) {
    item.push({
        title: d.title.zh||d.title.ja,
        url: 'https://bangumi.online/watch/' + d.vid + "#immersiveTheme##autoCache#",
        img: 'https:' + d.cover,
        desc: '第' + d.volume + '话'
    })
}

setResult(item);

